mapper.select:
select blbd_no ,blbd_strt_dt ,blbd_expr_dt ,blbd_type_cd ,fn_getCdNm('BLBD_TYPE_CD', blbd_type_cd) as blbd_type_nm ,blbd_titl ,blbd_cntn ,srch_cnt ,alam_yn ,reg_dt ,reg_tm ,reg_user_id ,upt_dt ,upt_tm ,upt_user_id from TS_BLBD_BASE where 1=1
and blbd_no = #{blbdNo}::numeric
and blbd_strt_dt <= #{blbdStrtDt} or blbd_expr_dt >= #{blbdExprDt}
and blbd_type_cd = #{blbdTypeCd}
and blbd_titl like concat('%', #{blbdTitl}, '%')
and blbd_cntn like concat('%', #{blbdCntn}, '%')

mapper.update:
UPDATE TS_BLBD_BASE
SET    UPT_DT      = TO_CHAR(NOW(), 'YYYYMMDD')
,UPT_TM      = TO_CHAR(NOW(), 'HH24MMIS')
,UPT_USER_ID = #{uptUserId}
,BLBD_NO = #{blbdNo}::numeric
,blbd_strt_dt  = #{blbdStrtDt}
,blbd_expr_dt  = #{blbdExprDt}
,blbd_type_cd  = #{blbdTypeCd}
,blbd_titl     = #{blbdTitl}
,blbd_cntn     = #{blbdCntn}
,srch_cnt      = #{srchCnt}::NUMERIC
,alam_yn       = #{alamYn}
WHERE  1 = 1
AND    BLBD_NO  = #{blbdNo}::numeric
