mapper.update:
	UPDATE TC_USER_BASE
	SET    UPT_DT      = TO_CHAR(NOW(), 'YYYYMMDD')
	      ,UPT_TM      = TO_CHAR(NOW(), 'HH24MMIS')
	      ,UPT_USER_ID = #{uptUserId}
	      ,USER_ID = #{userId}
    	,USER_NM      = ENCR(#{userNm})		
    	,BLNG_NM      = #{blngNm}		
    	,TEL_NO       = ENCR(#{telNo})				    
    	,MTEL_NO      = ENCR(#{mtelNo})	    	
    	,MAIL_ADDR    = ENCR(#{mailAddr})		
    	,ERTR_DT      = #{ertrDt}			        
    	,RELS_DT      = #{relsDt}			        
    	,USE_YN       = #{useYn}		     	
	     WHERE  1 = 1
	     AND      USER_ID = #{userId}

UPDATE TC_USER_BASE
SET    USER_PW     = #{userPw}
      ,UPT_DT      = TO_CHAR(NOW(), 'YYYYMMDD')
      ,UPT_TM      = TO_CHAR(NOW(), 'HH24MISS')
      ,UPT_USER_ID = #{uptUserId}
WHERE  USER_ID     = #{userId}


UPDATE TO_ACDM_BASE
SET    UPT_DT       = TO_CHAR(NOW(), 'YYYYMMDD')
      ,UPT_TM       = TO_CHAR(NOW(), 'HH24MMIS')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    ACDM_ID = #{userId}


UPDATE TO_LCTR_BASE
SET    UPT_DT       = DATE_FORMAT(NOW(), '%Y%m%d')
      ,UPT_TM       = DATE_FORMAT(NOW(), '%H%i%s')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    LCTR_ID = #{userId}


UPDATE TO_STDT_BASE
SET    UPT_DT       = DATE_FORMAT(NOW(), '%Y%m%d')
      ,UPT_TM       = DATE_FORMAT(NOW(), '%H%i%s')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    STDT_ID = #{userId}
