mapper.select:
with BAND_YTYP_WITH as ( select TBI.BAND_ID , case when cast(SUBSTRING(TBI.BAND_ID, 1,1) AS INTEGER) < 2 then '203'|| SUBSTRING(TBI.BAND_ID, 1,1) || SUBSTRING(TBI.BAND_ID, 2,2) else '202'|| SUBSTRING(TBI.BAND_ID, 1,1) || SUBSTRING(TBI.BAND_ID, 2,2) end AS BAND_YTYP --출고년월 , case when TBI.API_URL_DTTM is not null then 'Y' else 'N' end as API_URL_YN --URL제공여부 from TS_BAND_INFO as TBI ) select TBI.UPT_DT -- 기준일자 , TBI.REG_DT -- 등록일자 , T1.BAND_YTYP -- 출고년월 , TBI.BAND_MDL_CD -- 밴드_모델_코드 , TBI.BAND_ID -- 밴드_ID , TBI.TEL_NO -- 전화_번호(밴드) , TBI.BLTH_ID -- 블루투스_ID , TBI.BAND_OPEN_STAT_CD -- 밴드_개통_상태_코드 , fn_getCdNm('BAND_OPEN_STAT_CD', BAND_OPEN_STAT_CD) as BAND_OPEN_STAT_CD_NM -- 밴드_개통_상태_코드_명 , TSB.STDT_NO -- 학생_번호 , TSB.STDT_NM -- 학생_명 , TGB.GUAR_NO -- 보호자_번호 , TGB.GUAR_NM -- 보호자_명 , TBS.GUAR_TEL_NO -- 보호자_전화_번호 , TBS.GUAR_TEL_NO AS GUAR_TEL_NO_TEMP-- 보호자_전화_번호 , TBI.API_URL_GRAM_NO -- API URL 제공여부 , TBI.API_URL_DTTM -- URL_제공_일시 , TBI.OPEN_GRAM_NO -- 개통_전문_번호 , T1.API_URL_YN -- URL_제공_여부 , TBI.REG_DT , TBI.REG_TM , TBI.REG_USER_ID , TBI.UPT_DT , TBI.UPT_TM , TBI.UPT_USER_ID from TS_BAND_INFO TBI inner join TS_BAND_SPEC TBS on TBI.BAND_ID = TBS.BAND_ID left outer join TM_STDT_BASE TSB on TBI.BAND_ID = TSB.BAND_ID left outer join TM_GUAR_BASE TGB on TBS.GUAR_TEL_NO = TGB.GUAR_TEL_NO left outer join BAND_YTYP_WITH AS T1 on TBI.BAND_ID = T1.BAND_ID where 1=1
and TBI.UPT_DT between #{uptDtFr} and #{uptDtTo}
and TSB.STDT_NM like CONCAT('%',#{stdtNm},'%')
and TBI.TEL_NO = #{telNo}
and T1.BAND_YTYP like CONCAT('%',#{bandYtypCd},'%')
and TBI.BAND_ID = #{bandId}
and TBI.BAND_OPEN_STAT_CD = #{bandOpenStatCd}
and TBS.GUAR_TEL_NO = #{guarTelNo}
and TGB.GUAR_NM like CONCAT('%',#{guarNm},'%') ORDER BY TBI.UPT_DT, TBI.REG_DT, T1.BAND_YTYP, TBI.BAND_ID, TGB.GUAR_NO, TGB.GUAR_NM
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
UPDATE TS_BAND_INFO
SET UPT_DT = TO_CHAR(NOW(), 'YYYYMMDD')
, UPT_TM = TO_CHAR(NOW(),'HH24MISS')
, UPT_USER_ID = #{uptUserId}
 ,TEL_NO = #{telNo}
 ,BAND_OPEN_STAT_CD = #{bandOpenStatCd}
 ,BLTH_ID = #{blthId}
WHERE 1=1
AND BAND_ID = #{bandId}

/* Generated SQL SQL ID : ts_band_spec.update */
UPDATE TS_BAND_SPEC
SET    UPT_DT = TO_CHAR(NOW(), 'YYYYMMDD')
     , UPT_TM = TO_CHAR(NOW(),'HH24MISS')
     , UPT_USER_ID = #{uptUserId}
 ,GUAR_TEL_NO = #{guarTelNo}
WHERE  1 = 1
AND    BAND_ID = #{bandId} /* band_id 밴드_ID character varying(20) */
AND    GUAR_TEL_NO = #{guarTelNoTemp} /* guar_tel_no 보호자_전화_번호 character varying(20) */
