mapper.select:
select tbi.ble_id ,tbi.ble_inst_dt ,tbi.ble_inst_tm ,tbi.loc_no ,tbi.rmrk ,tlib.plc_cd ,fn_getcdnm('PLC_CD',tlib.plc_cd) as plc_nm ,tlib.loc_nm ,tlib.addr_base||tlib.addr_spec as addr_base from ts_ble_info tbi left outer join ts_loc_info_base tlib on tbi.loc_no = tlib.loc_no where 1=1
and to_char(to_date(ble_inst_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{bleInstDtFr} and #{bleInstDtTo}
and tlib.loc_nm like '%'||#{eorgNm}||'%'
and tlib.addr_base||tlib.addr_spec like '%'||#{addr}||'%'
order by tbi.ble_id
LIMIT ${rowCount} OFFSET ${currentIndex}

select count(1) as dup_cnt
from ts_ble_info tbi
where ble_id = #{bleId}
