mapper.select:
select A.STDT_NO ,DECR(A.STDT_NM) as STDT_NM ,C.GUAR_NO ,DECR(C.GUAR_NM) as GUAR_NM ,DECR(C.GUAR_TEL_NO) as GUAR_TEL_NO from TM_STDT_BASE A left outer join TM_STDT_GUAR_CONN B on A.STDT_NO = B.STDT_NO left outer join TM_GUAR_BASE C on B.GUAR_NO = C.GUAR_NO where 1=1
and A.STDT_NO = #{stdtNo}::numeric
and DECR(A.STDT_NM) = #{stdtNm}
and C.GUAR_NO = #{guarNo}::numeric
and DECR(C.GUAR_NM) = #{guarNm}
and DECR(C.GUAR_TEL_NO) = #{guarTelNo} group by A.STDT_NO , A.STDT_NM , C.GUAR_NO , C.GUAR_NM order by A.stdt_no
LIMIT ${rowCount} OFFSET ${currentIndex}

select TEB.LOC_NO ,TEB.LOC_NM ,TEB.ROAD_ADDR from TI_EORG_BASE TEB left outer join TM_STDT_BASE TSB on TSB.EORG_LOC_NO = TEB.LOC_NO where 1=1
and SPLIT_PART(TEB.ROAD_ADDR, ' ', 1) = #{wordHead1}
and SPLIT_PART(TEB.ROAD_ADDR, ' ', 2) = #{wordHead2}
and TEB.LOC_NM like CONCAT ('%',#{locNm},'%') order by TEB.LOC_NO
LIMIT ${rowCount} OFFSET ${currentIndex}
