mapper.select:
select TAH.SEND_DTTM /*발생일시*/ ,TAH.GUAR_NO /*보호자번호*/ ,TGB.GUAR_NM /*보호자명*/ ,TBS.GUAR_TEL_NO /*보호자전화번호*/ ,TSB.STDT_NO /*학생번호*/ ,TSB.STDT_NM /*학생명*/ ,TAH.ALAM_CHNL_CD /*채널종류*/ ,TAH.ALAM_TYPE_CD /*알림유형*/ ,TAH.ALAM_TITL /*알림제목*/ ,case when TAH.SEND_RSLT_CD = '00' then '성공' /*발송여부*/ when TAH.SEND_RSLT_CD = '01' then '미발송' when TAH.SEND_RSLT_CD = '11' then 'key없음' when TAH.SEND_RSLT_CD = '99' then '발송 실패(기타)' end as SEND_RSLT_CD from TS_ALAM_HIST TAH left outer join TM_STDT_BASE TSB on TAH.STDT_NO = TSB.STDT_NO left outer join TS_BAND_SPEC TBS on TBS.BAND_ID = TSB.BAND_ID left outer join TM_GUAR_BASE TGB on TGB.GUAR_NO = TAH.GUAR_NO where 1=1
and TAH.SEND_DTTM between #{entrDtFr} and #{entrDtTo}
and TAH.ALAM_CHNL_CD = #{alamChnlCd}
and TAH.ALAM_TYPE_CD = #{alamTypeCd}
and TAH.SEND_RSLT_CD = #{sendRsltCd}
and TAH.GUAR_NO = #{guarNo}
and TAH.GUAR_NM = #{guarNm}
LIMIT ${rowCount} OFFSET ${currentIndex}

select A.STDT_NO ,A.STDT_NM ,C.GUAR_NO ,C.GUAR_NM ,C.GUAR_TEL_NO from TM_STDT_BASE A left outer join TM_STDT_GUAR_CONN B on A.STDT_NO = B.STDT_NO left outer join TM_GUAR_BASE C on B.GUAR_NO = C.GUAR_NO where 1=1
and A.STDT_NO = #{stdtNo}::numeric
and A.STDT_NM = #{stdtNm}
and C.GUAR_NO = #{guarNo}::numeric
and C.GUAR_NM = #{guarNm}
and C.GUAR_TEL_NO = #{guarTelNo} group by A.STDT_NO , A.STDT_NM , C.GUAR_NO , C.GUAR_NM order by A.stdt_no
LIMIT ${rowCount} OFFSET ${currentIndex}

select TEB.LOC_NO ,TEB.LOC_NM ,TEB.ROAD_ADDR from TI_EORG_BASE TEB left outer join TM_STDT_BASE TSB on TSB.EORG_LOC_NO = TEB.LOC_NO where 1=1
and SPLIT_PART(TEB.ROAD_ADDR, ' ', 1) = #{wordHead1}
and SPLIT_PART(TEB.ROAD_ADDR, ' ', 2) = #{wordHead2}
and TEB.LOC_NM like CONCAT ('%',#{locNm},'%') order by TEB.LOC_NO
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
/* updateTiGrowJudgStnd */
UPDATE tm_stdt_base


      ,stdt_nm = #{stdtNm}


      ,band_id = #{bandId}


      ,eorg_loc_no = cast(#{eorgLocNo} as integer)


      ,sgrd_cd = #{sgrdCd}


      ,sex_cd = #{sexCd}


      ,bith_dt = #{bithDt}


      ,race_div_cd = #{raceDivCd}

  ,upt_dt = TO_CHAR(NOW(), 'YYYYMMDD') /* upt_dt 수정_일자 character(8) */
  ,upt_tm = TO_CHAR(NOW(), 'HH24MISS')  /* upt_tm 수정_시각 character(6) */
  ,upt_user_id = #{uptUserId} /* upt_user_id 수정_사용자_ID character varying(20) */

WHERE  1 = 1
AND    stdt_no = #{stdtNo}

/* updateTsStdtGrowHist */
UPDATE ts_stdt_grow_hist


        ,hght_val = #{hghtVal}


        ,wght_val = #{wghtVal}


        ,wast_val = #{wastVal}

    ,upt_dt = TO_CHAR(NOW(), 'YYYYMMDD') /* upt_dt 수정_일자 character(8) */
    ,upt_tm = TO_CHAR(NOW(), 'HH24MISS')  /* upt_tm 수정_시각 character(6) */
    ,upt_user_id = #{uptUserId} /* upt_user_id 수정_사용자_ID character varying(20) */

WHERE  1 = 1
AND    stdt_no = #{stdtNo}
AND    mesu_dt = #{growMesuDt}
