mapper.select:
SELECT tssh.strs_judg_dttm /* strs_judg_dttm 스트레스_판정_일시 character(14) */ , teb.loc_nm /*loc_nm 학교_명 varchar(100) */ , tssh.stdt_no /* stdt_no 학생_번호 numeric */ , decr(tsb.stdt_nm) as stdt_nm /*stdt_nm 학생_명 varchar(40) */ , fn_getcdnm('SEX_CD', tsb.sex_cd) as sex_cd_nm --성별_코드_명 , fn_getage(tsb.bith_dt) as age_yy --나이(년수) , fn_getagem(tsb.bith_dt) as age_mm --나이(개월수) , fn_getcdnm('STRS_STAT_CD' , tssh.strs_stat_cd) as strs_stat_cd_nm --스트레스_상태_코드_명 , fn_getcdnm('STRS_STAT_CD' , tssh.mind_strs_stat_cd) as mind_strs_stat_cd_nm --정신적_스트레스_상태_코드_명 , fn_getcdnm('STRS_STAT_CD' , tssh.phys_strs_stat_cd) as phys_strs_stat_cd_nm --신체적_스트레스_상태_코드_명 , fn_getcdnm('STRS_COPE_STAT_CD',tssh.strs_cope_stat_cd) as strs_cope_stat_cd --스트레스_대처_상태_코드_명 , decr(tbi.tel_no) as tel_no --밴드_전화_번호 , tgb.guar_no --보호자_번호 , decr(tgb.guar_nm) as guar_nm --보호자_명 , decr(tgb.guar_tel_no) as guar_tel_no --보호자_전화_번호 FROM TS_STDT_STRS_HIST tssh --학생_스트레스_이력 inner join TM_STDT_BASE tsb --학생_기본 tsb on tssh.STDT_NO = tsb.STDT_NO inner join TS_BAND_INFO tbi --밴드_정보 tbi on tsb.BAND_ID = tbi.BAND_ID inner join TM_STDT_GUAR_CONN tsgc--학생_보호자_연결 tsgc on tsgc.STDT_NO = tsb.STDT_NO inner join TM_GUAR_BASE tgb --보호자_기본 tgb on tsgc.GUAR_NO = tgb.GUAR_NO left outer join TI_EORG_BASE teb --교육기관_기본 teb on tsb.EORG_LOC_NO = teb.LOC_NO WHERE 1 = 1 /*발생 일시*/ and tssh.strs_judg_dttm between replace(#{strsJudgDttmFr},'-','') and replace(#{strsJudgDttmTo},'-','') /*학생_번호*/ and tssh.stdt_no = #{stdtNo}::NUMERIC /*학생_명*/ and decr(tsb.stdt_NM) like CONCAT('%',#{stdtNm},'%') /*나이*/ and fn_getage(tsb.bith_dt) between #{ageFr}::NUMERIC and #{ageTo}::NUMERIC /*성별*/ and tsb.sex_cd = #{sexCd} /*보호자_번호*/ and tgb.guar_no = #{guarNo}::NUMERIC /*보호자_명*/ and decr(tsb.guar_NM) like CONCAT('%',#{guarNm},'%') /*스트레스_상태_코드*/ and tssh.strs_stat_cd like CONCAT('%',#{strsStatCd},'%') /*정신적_스트레스_상태_코드*/ and tssh.mind_strs_stat_cd like CONCAT('%',#{mindStrsStatCd},'%') /*신체적_스트레스_상태_코드*/ and tssh.phys_strs_stat_cd_nm like CONCAT('%',#{physStrsStatCd},'%') /*스트레스_대처_상태_코드*/ and tssh.strs_cope_stat_cd like CONCAT('%',#{strsCopeStatCd},'%')
and tsb.LOC_NM like CONCAT('%',#{locNm},'%') /*학교_명*/ group by tdeh.stnd_dt, teb.loc_nm , tdeh.stdt_no, tsb.stdt_nm, tdeh.guar_no, tbi.tel_no , tgb.guar_nm , tgb.guar_tel_no ) fmenuData where 1=1 /*아침,점심,저녁,간식_식사_여부*/ and fmenuData.mmel_yn = #{mmelYn} or fmenuData.amel_yn = #{amelYn} or fmenuData.emel_yn = #{emelYn} or fmenuData.smel_yn = #{smelYn}
and fmenuData.mmel_yn = #{mmelStarvYn} /*아침_결식_여부*/ group by fmenuData.stnd_dt, fmenuData.loc_nm , fmenuData.stdt_no, fmenuData.stdt_nm, fmenuData.guar_no, fmenuData.tel_no , fmenuData.guar_nm , fmenuData.guar_tel_no
LIMIT ${rowCount} OFFSET ${currentIndex}
