mapper.select:
SELECT TFB.GUAR_NO as GUAR_NO /* GUAR_NO 보호자_번호 numeric */ , DECR(TGB.GUAR_NM) as GUAR_NM /* GUAR_NM 보호자_명 character varying(40) */ , TFB.FMENU_SEQ as FMENU_SEQ /* FMENU_SEQ 식단표_순번 numeric */ , TFB.FMENU_NM as FMENU_NM /* FMENU_NM 식단표_명 character varying(100) */ , TFB.MMEL_YN as MMEL_YN /* MMEL_YN 아침_여부 character(1) */ , TFB.AMEL_YN as AMEL_YN /* AMEL_YN 점심_여부 character(1) */ , TFB.EMEL_YN as EMEL_YN /* EMEL_YN 저녁_여부 character(1) */ , TFB.SMEL_YN as SMEL_YN /* SMEL_YN 간식_여부 character(1) */ , TDEH.FEQ_USE as FEQ_USE /*이용 빈도(수)*/ FROM TS_FMENU_BASE TFB --식단표_기본 TFB INNER JOIN TM_GUAR_BASE TGB --보호자_기본 TGB ON TFB.GUAR_NO = TGB.GUAR_NO LEFT OUTER JOIN ( SELECT count(FMENU_SEQ) as FEQ_USE , GUAR_NO , FMENU_SEQ FROM TS_DD_EAT_HIST GROUP BY GUAR_NO,FMENU_SEQ ) TDEH --일별_섭취_이력 TDEH ON TFB.GUAR_NO = TDEH.GUAR_NO AND TFB.FMENU_SEQ = TDEH.FMENU_SEQ WHERE 1 = 1 /*식단표_명*/ AND TFB.FMENU_NM like CONCAT('%',#{fmenuNm},'%') /*포함_음식1*/ AND TFB.FMENU_SEQ IN (SELECT TFB.FMENU_SEQ FROM TS_FMENU_BASE TFB INNER JOIN TS_FMENU_SPEC TFS ON TFB.GUAR_NO = TFS.GUAR_NO and TFB.FMENU_SEQ = TFS.FMENU_SEQ INNER JOIN TI_FOOD_INFO TFI ON TFS.FOOD_NO = TFI.FOOD_NO WHERE TFB.GUAR_NO = #{guarNo} AND TFI.FOOD_NM LIKE CONCAT('%',#{foodNm1},'%')) OR TFI.FOOD_NM LIKE CONCAT('%',#{foodNm2},'%'))
/*아침,점심,저녁,간식_여부*/ AND (
OR TFB.MMEL_YN = #{mmelYn}
OR TFB.AMEL_YN = #{amelYn}
OR TFB.EMEL_YN = #{emelYn}
OR TFB.SMEL_YN = #{smelYn}
)
/*보호자_번호*/ AND TFB.GUAR_NO = #{guarNo}::NUMERIC /*보호자_명*/ AND TGB.GUAR_NM = ENCR(#{guarNm})
ORDER BY TGB.GUAR_NO, TGB.GUAR_NM, TFB.FMENU_SEQ LIMIT ${rowCount} OFFSET ${currentIndex}
