mapper.select:
select tlib.loc_no ,tlib.loc_nm ,addr_base ,tlib.plc_cd ,fn_getcdnm('PLC_CD' , tlib.plc_cd) as PLC_NM ,tlib.plc_clss_cd ,fn_getcdnm('PLC_CLSS_CD' , tlib.plc_clss_cd) as plc_clss_nm from ts_loc_info_base tlib where 1=1 and plc_clss_cd = 'SZONE'
and split_part(tlib.addr_base, ' ', 1) = #{wordHead1}
and split_part(tlib.addr_base, ' ', 2) = #{wordHead2}
and tlib.loc_nm like concat ('%',#{locNm},'%')
and tlib.addr_base like concat ('%',#{locNm},'%')
LIMIT ${rowCount} OFFSET ${currentIndex}
