mapper.update:

	UPDATE TC_CD_GRP
	SET    UPT_DT   = TO_CHAR(NOW(), 'YYYYMMDD')
	      ,UPT_TM      = TO_CHAR(NOW(), 'HH24IISS')
	      ,UPT_USER_ID = #{uptUserId}
    	,CD_GRP_NM = #{cdGrpNm}	
    	,USE_YN    = #{useYn}		
	WHERE  CD_GRP = #{cdGrp}

mapper.select:
SELECT B.CD_GRP ,B.CD_GRP_NM ,A.CD_VAL ,A.CD_NM ,A.CD_DESC ,A.FLTR_VAL_1 ,A.FLTR_VAL_2 ,A.FLTR_VAL_3 ,A.SORT_ORD ,A.USE_YN FROM TC_CD_SPEC A INNER JOIN TC_CD_GRP B ON B.CD_GRP = A.CD_GRP WHERE 1 = 1
AND A.CD_GRP = #{cdGrp}
AND A.CD_VAL = #{cdVal} ORDER BY A.CD_GRP, A.SORT_ORD
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.insert:

	INSERT INTO TC_CD_SPEC
	(
	     CD_GRP
	    ,CD_VAL
	    ,CD_NM
	    ,CD_DESC
	    ,FLTR_VAL_1
	    ,FLTR_VAL_2
	    ,FLTR_VAL_3
	    ,SORT_ORD
	    ,USE_YN
	    ,REG_DT
	    ,REG_TM
	    ,REG_USER_ID
	    ,UPT_DT
	    ,UPT_TM
	    ,UPT_USER_ID
	)
	VALUES
	(
	     #{cdGrp}
	    ,#{cdVal}
	    ,#{cdNm}
	    ,#{cdDesc}
	    ,#{fltrVal1}
	    ,#{fltrVal2}
	    ,#{fltrVal3}
       ,CAST (#{sortOrd} AS INTEGER)
       ,null
	   ,#{useYn}
	    ,TO_CHAR(NOW(), 'YYYYMMDD')
	    ,TO_CHAR(NOW(), 'HH24IISS')
	    ,#{regUserId}
	    ,TO_CHAR(NOW(), 'YYYYMMDD')
	    ,TO_CHAR(NOW(), 'HH24IISS')
	    ,#{uptUserId}	
	)

mapper.update:

	UPDATE TC_CD_SPEC
	SET    UPT_DT    = TO_CHAR(NOW(), 'YYYYMMDD')
	      ,UPT_TM      = TO_CHAR(NOW(), 'HH24IISS')
	      ,UPT_USER_ID = #{uptUserId}
    	,CD_GRP    = #{newCdGrp}	
    	,CD_VAL    = #{newCdVal}	
    	,CD_NM    = #{cdNm}	
    	,CD_DESC   = #{cdDesc}	
    	,FLTR_VAL_1   = #{fltrVal1}	
    	,FLTR_VAL_2   = #{fltrVal2}	
    	,FLTR_VAL_3   = #{fltrVal3}	
    	,SORT_ORD =    CAST (#{sortOrd} AS INTEGER)	
    	,USE_YN   = #{useYn}	
 	WHERE  CD_GRP = #{oldCdGrp}
	AND    CD_VAL = #{oldCdVal}

mapper.delete:

DELETE
FROM   TC_CD_SPEC
WHERE  CD_GRP = #{cdGrp}

AND    CD_VAL = #{cdVal}
