mapper.select:
SELECT CD_GRP ,CD_GRP_NM ,USE_YN FROM TC_CD_GRP WHERE 1 = 1
AND CD_GRP = #{cdGrp}
AND CD_GRP_NM LIKE CONCAT('%', #{cdGrpNm}, '%') ORDER BY CD_GRP
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:

	UPDATE TC_CD_GRP
	SET    UPT_DT      = DATE_FORMAT(NOW(), '%Y%m%d')
	      ,UPT_TM      = DATE_FORMAT(NOW(), '%H%i%s')
	      ,UPT_USER_ID = #{uptUserId}
    	,CD_GRP_NM = #{cdGrpNm}	
    	,USE_YN    = #{useYn}		
	WHERE  CD_GRP = #{cdGrp}

mapper.select:
SELECT B.CD_GRP ,B.CD_GRP_NM ,A.CD_VAL ,A.CD_NM ,A.SORT_ORD ,A.USE_YN FROM TC_CD_SPEC A INNER JOIN TC_CD_GRP B ON B.CD_GRP = A.CD_GRP WHERE 1 = 1
AND A.CD_GRP = #{cdGrp}
AND A.CD_VAL = #{cdVal} ORDER BY A.CD_GRP, A.SORT_ORD
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:

	UPDATE TC_CD_SPEC
	SET    UPT_DT      = DATE_FORMAT(NOW(), '%Y%m%d')
	      ,UPT_TM      = DATE_FORMAT(NOW(), '%H%i%s')
	      ,UPT_USER_ID = #{uptUserId}
    	,CD_NM    = #{cdNm}	
    	,SORT_ORD = #{sortOrd}	
    	,USE_YN   = #{useYn}	
	WHERE  CD_GRP = #{cdGrp}
	AND    CD_VAL = #{cdVal}

mapper.delete:

DELETE
FROM   TC_CD_SPEC
WHERE  CD_GRP = #{cdGrp}

AND    CD_VAL = #{cdVal}
