mapper.select:
SELECT A.MENU_NO ,B.MENU_NM ,B.UPPR_MENU_NO ,D.MENU_NM AS UPPR_MENU_NM ,A.ROLE_CD ,C.ROLE_NM FROM TC_MENU_ROLE A INNER JOIN TC_MENU_BASE B ON B.MENU_NO = A.MENU_NO INNER JOIN TC_ROLE_BASE C ON C.ROLE_CD = A.ROLE_CD INNER JOIN TC_MENU_BASE D ON D.MENU_NO = B.UPPR_MENU_NO WHERE 1 = 1
AND A.MENU_NO = #{menuNo}
AND A.ROLE_CD = #{roleCd} ORDER BY A.MENU_NO
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
	UPDATE TC_MENU_ROLE
	SET    UPT_DT      = DATE_FORMAT(NOW(), '%Y%m%d')
	      ,UPT_TM      = DATE_FORMAT(NOW(), '%H%i%s')
	      ,UPT_USER_ID = #{uptUserId}
    	,ROLE_CD = #{roleCd}	
	WHERE  1 = 1

	AND    MENU_NO = #{menuNo}		

	AND    ROLE_CD = #{roleCd}

mapper.delete:
DELETE
FROM   TC_MENU_ROLE
WHERE  1 = 1

AND    MENU_NO = #{menuNo}		

AND    ROLE_CD = #{roleCd}
