mapper.select:
SELECT USER_ID ,USER_NM ,USER_PW ,BLNG_NM ,TEL_NO ,MTEL_NO ,MAIL_ADDR ,ENTR_DT ,RELS_DT ,USE_YN FROM TC_USER_BASE WHERE 1 = 1
AND USER_ID = #{userId}
AND USER_NM LIKE CONCAT('%', #{userNm}, '%')
AND USER_PW = #{userPw}
AND BLNG_NM LIKE CONCAT ('%', #{blngNm}, '%')
AND TEL_NO LIKE CONCAT ('%', #{telNo}, '%')
AND MTEL_NO LIKE CONCAT ('%', #{mtelNo}, '%')
AND MAIL_ADDR LIKE CONCAT ('%', #{mailAddr}, '%')
AND USE_YN LIKE CONCAT('%',#{useYn},'%') ORDER BY USER_ID
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
	UPDATE TC_USER_BASE
	SET    UPT_DT      = TO_CHAR(NOW(), 'YYYYMMDD')
	      ,UPT_TM      = TO_CHAR(NOW(), 'HH24MMIS')
	      ,UPT_USER_ID = #{uptUserId}
	      ,USER_ID = #{userId}
    	,USER_NM      = #{userNm}		
    	,BLNG_NM      = #{blngNm}		
    	,TEL_NO       = #{telNo}				    
    	,MTEL_NO      = #{mtelNo}	    	
    	,MAIL_ADDR    = #{mailAddr}		
    	,ERTR_DT      = #{ertrDt}			        
    	,RELS_DT      = #{relsDt}			        
    	,USE_YN       = #{useYn}		     	
	     WHERE  1 = 1
	     AND      USER_ID = #{userId}

UPDATE TC_USER_BASE
SET    USER_PW     = #{userPw}
      ,UPT_DT      = TO_CHAR(NOW(), 'YYYYMMDD')
      ,UPT_TM      = TO_CHAR(NOW(), 'HH24MISS')
      ,UPT_USER_ID = #{uptUserId}
WHERE  USER_ID     = #{userId}


UPDATE TO_ACDM_BASE
SET    UPT_DT       = TO_CHAR(NOW(), 'YYYYMMDD')
      ,UPT_TM       = TO_CHAR(NOW(), 'HH24MMIS')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    ACDM_ID = #{userId}


UPDATE TO_LCTR_BASE
SET    UPT_DT       = DATE_FORMAT(NOW(), '%Y%m%d')
      ,UPT_TM       = DATE_FORMAT(NOW(), '%H%i%s')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    LCTR_ID = #{userId}


UPDATE TO_STDT_BASE
SET    UPT_DT       = DATE_FORMAT(NOW(), '%Y%m%d')
      ,UPT_TM       = DATE_FORMAT(NOW(), '%H%i%s')
      ,UPT_USER_ID  = #{userId}
      ,PROF_IMG_NO  = #{fileNo}
WHERE  1 = 1
AND    STDT_ID = #{userId}
