mapper.select:
select x.stnd_dt from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt from TT_DGEM_IDX_STSS where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

select  dgem_stat_cd as cd_val
,tcs.cd_nm
from TT_DGEM_STAT_STSS tgjs
inner join tc_cd_spec tcs
on tgjs.dgem_stat_cd = tcs.cd_val
and tcs.cd_grp = 'DGEM_STAT_CD'
where 1=1

    and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}



    and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



    and sex_cd = #{sexCd}


    and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

group by tgjs.dgem_stat_cd, tcs.cd_nm, tcs.sort_ord
order by tcs.sort_ord

select x.stnd_dt ,round( avg(x.avg_idx) , 2) as avg_idx from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,avg_idx from TT_DGEM_IDX_STSS where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

with tt_dgem_stat_stss_temp as ( select dgem_stat_cd ,sum(occr_cnt) as occr_cnt from tt_dgem_stat_stss tgjs where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
group by dgem_stat_cd ) select dgem_stat_cd ,occr_cnt ,sum(occr_cnt) over() as tot_cnt ,round(sum(occr_cnt) over(partition by dgem_stat_cd) / sum(occr_cnt) over() * 100 , 2) as occr_per from tt_dgem_stat_stss_temp tgjs where 1=1 order by dgem_stat_cd
