mapper.select:
select x.stnd_dt
from (select case when #{perdDivCd} = 'MONTH'   then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM')
                  when #{perdDivCd} = 'WEEK'    then to_char(to_date(stnd_yymm , 'YYYYMM') , '`YY.MM') || ' ' || mnth_week_seq ||'주차'
                  when #{perdDivCd} = 'WEEK_NM' then week_nm
else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt
from TT_ZONE_DTCT_STSS
where 1=1

    and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}




    and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



    and sex_cd = #{sexCd}



    and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

) x


        inner join  tc_cd_spec tcs
        on x.stnd_dt = tcs.cd_val
        and tcs.cd_grp = 'WEEK_CD'
        where 1=1
        group by x.stnd_dt , tcs.sort_ord
        order by tcs.sort_ord ,  x.stnd_dt


        group by x.stnd_dt
        order by x.stnd_dt

select x.stnd_dt ,x.occr_cnt ,x.stdt_cnt from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , '`YY.MM') || ' ' || mnth_week_seq ||'주차' when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,occr_cnt ,stdt_cnt from TT_ZONE_DTCT_STSS where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord , x.occr_cnt , x.stdt_cnt order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt ,x.occr_cnt ,x.stdt_cnt order by x.stnd_dt
