mapper.select:
select x.stnd_dt
from (select case when #{perdDivCd} = 'MONTH'   then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM')
when #{perdDivCd} = 'WEEK'    then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq
when #{perdDivCd} = 'WEEK_NM' then week_nm
else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt
from tt_act_stss
where 1=1

    and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}




    and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



    and sex_cd = #{sexCd}



    and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

) x


        inner join  tc_cd_spec tcs
        on x.stnd_dt = tcs.cd_val
        and tcs.cd_grp = 'WEEK_CD'
        where 1=1
        group by x.stnd_dt , tcs.sort_ord
        order by tcs.sort_ord ,  x.stnd_dt


        group by x.stnd_dt
        order by x.stnd_dt

select x.stnd_dt ,round( avg(x.avg_act_tcnt_mcnt) , 2) as avg_act_tcnt_mcnt ,round( avg(x.avg_cal_csum_qty) , 2) as avg_cal_csum_qty from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,avg_act_tcnt_mcnt ,avg_cal_csum_qty from tt_act_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt
