mapper.select:
select x.stnd_dt
from (select case when #{perdDivCd} = 'MONTH'   then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM')
                  when #{perdDivCd} = 'WEEK'    then to_char(to_date(stnd_yymm , 'YYYYMM') , '`YY.MM') || ' ' || mnth_week_seq ||'주차'
                  when #{perdDivCd} = 'WEEK_NM' then week_nm
                  else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt
from tt_act_stss
where 1=1

    and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}




    and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



    and sex_cd = #{sexCd}



    and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

) x


        inner join  tc_cd_spec tcs
        on x.stnd_dt = tcs.cd_val
        and tcs.cd_grp = 'WEEK_CD'
        where 1=1
        group by x.stnd_dt , tcs.sort_ord
        order by tcs.sort_ord ,  x.stnd_dt


        group by x.stnd_dt
        order by x.stnd_dt

with tt_act_stss_temp as (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(x.stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , '`YY.MM') || ' ' || mnth_week_seq ||'주차' when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(x.stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,x.avg_act_tcnt_mcnt ,x.avg_cal_csum_qty ,x.act_cd ,tas.act_nm from tt_act_stss x inner join ti_act_stnd tas on x.act_cd = tas.act_cd where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
), tt_act_stss_all as (select x.stnd_dt ,'ALL' as act_div_cd ,'전체' as act_div_nm ,round( avg(x.avg_act_tcnt_mcnt) , 2) as avg_act_tcnt_mcnt ,round( avg(x.avg_cal_csum_qty) , 2) as avg_cal_csum_qty from tt_act_stss_temp x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt
), tt_act_stss_act as ( select x.stnd_dt ,x.act_cd as act_div_cd ,x.act_nm as act_div_nm ,round( avg(x.avg_act_tcnt_mcnt) , 2) as avg_act_tcnt_mcnt ,round( avg(x.avg_cal_csum_qty) , 2) as avg_cal_csum_qty from tt_act_stss_temp x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , x.act_cd, x.act_nm, tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt, x.act_cd, x.act_nm order by x.stnd_dt
) select stnd_dt ,act_div_cd ,act_div_nm ,avg_act_tcnt_mcnt ,avg_cal_csum_qty from tt_act_stss_all union all select stnd_dt ,act_div_cd ,act_div_nm ,avg_act_tcnt_mcnt ,avg_cal_csum_qty from tt_act_stss_act
