mapper.select:
select x.stnd_dt from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt from tt_fatp_idx_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

select  fatp_judg_cd as cd_val
       ,tcs.cd_nm
from tt_fatp_judg_stss tfjs
inner join tc_cd_spec tcs
on tfjs.fatp_judg_cd = tcs.cd_val
and tcs.cd_grp = 'FAT_JUDG_CD'
where 1=1

        and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}



        and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



        and sex_cd = #{sexCd}


        and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

group by tfjs.fatp_judg_cd, tcs.cd_nm, tcs.sort_ord
order by tcs.sort_ord

select x.stnd_dt ,round( avg(x.avg_idx) , 2) as avg_idx from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,avg_idx from tt_fatp_idx_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

with ts_gfix_judg_stss_temp as ( select fatp_judg_cd ,sum(occr_cnt) as occr_cnt from tt_fatp_judg_stss tfjs where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
group by fatp_judg_cd ) select fatp_judg_cd ,occr_cnt ,sum(occr_cnt) over() as tot_cnt ,round(sum(occr_cnt) over(partition by fatp_judg_cd) / sum(occr_cnt) over() * 100 , 2) as occr_per from ts_gfix_judg_stss_temp tfjs where 1=1 order by fatp_judg_cd
