mapper.select:
select x.stnd_dt from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt from tt_neat_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

with tt_neat_stss_temp as ( select x.stnd_dt , count(x.OBJ_CNT) as OBJ_CNT , round(sum(x.NEAT_CNT)/count(x.OBJ_CNT),2) as NEAT_CNT from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,OBJ_CNT ,NEAT_CNT from tt_neat_stss where 1=1
and eagr_div_cd = #{eagrDivCd}
and etim_div_cd = #{mmelYn}
or etim_div_cd = #{amelYn}
or etim_div_cd = #{emelYn}
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt
)select stnd_dt as stnd_dt , ROUND(avg(NEAT_CNT),2) as NEAT_CNT , ROUND(NEAT_CNT/0.07,2) as NEAT_PER from tt_neat_stss_temp group by STND_DT ,ROUND(NEAT_CNT/0.07,2)
