mapper.select:
select x.stnd_dt from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt from tt_strs_idx_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
and strs_type_cd = #{strsTypeCd}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt , tcs.sort_ord order by tcs.sort_ord , x.stnd_dt
group by x.stnd_dt order by x.stnd_dt

select  strs_stat_cd as cd_val
       ,tcs.cd_nm
from tt_strs_judg_stss tsjs
inner join tc_cd_spec tcs
on tsjs.strs_stat_cd = tcs.cd_val


        and tcs.cd_grp = 'STRS_COPE_STAT_CD'


        and tcs.cd_grp = 'STRS_STAT_CD'


where 1=1

        and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}



        and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}


        and sex_cd = #{sexCd}


        and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

    and strs_type_cd = #{subStrsTypeCd}
group by tsjs.strs_stat_cd, tcs.cd_nm, tcs.sort_ord
order by tcs.sort_ord

select x.stnd_dt ,x.strs_type_cd ,fn_getCdnm('STRS_TYPE_CD' , x.strs_type_cd ) as strs_type_nm ,round( avg(x.avg_idx) , 2) as avg_idx from (select case when #{perdDivCd} = 'MONTH' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') when #{perdDivCd} = 'WEEK' then to_char(to_date(stnd_yymm , 'YYYYMM') , 'YYYY-MM') || '-' || mnth_week_seq when #{perdDivCd} = 'WEEK_NM' then week_nm else to_char(to_date(stnd_dt , 'YYYYMMDD') , 'YYYY-MM-DD') end as stnd_dt ,strs_type_cd ,avg_idx from tt_strs_idx_stss where 1=1
and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}
and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}
and sex_cd = #{sexCd}
and age_ycnt between ${ageYcntFr} and ${ageYcntTo}
and strs_type_cd = #{strsTypeCd}
) x
inner join tc_cd_spec tcs on x.stnd_dt = tcs.cd_val and tcs.cd_grp = 'WEEK_CD' where 1=1 group by x.stnd_dt ,x.strs_type_cd, tcs.sort_ord order by tcs.sort_ord , x.strs_type_cd , x.stnd_dt
group by x.stnd_dt , x.strs_type_cd order by x.strs_type_cd, x.stnd_dt

 with ts_strs_judg_stss_temp  as
( select
      strs_stat_cd
   ,sum(occr_cnt) as occr_cnt
  from tt_strs_judg_stss tsjs
  where 1=1

        and to_char(to_date(stnd_dt , 'YYYYMMDD') ,'YYYY-MM-DD' ) between #{stndDtFr} and #{stndDtTo}



        and to_char(to_date(stnd_yymm , 'YYYYMM') ,'YYYY-MM' ) between #{stndMmFr} and #{stndMmTo}



        and sex_cd = #{sexCd}


        and age_ycnt between ${ageYcntFr} and ${ageYcntTo}

    and strs_type_cd = #{subStrsTypeCd}
  group by  strs_stat_cd
)
select  strs_stat_cd
       ,occr_cnt
       ,sum(occr_cnt) over()                          as tot_cnt
       ,round(sum(occr_cnt) over(partition by strs_stat_cd) / sum(occr_cnt) over() * 100 , 2) as occr_per
from ts_strs_judg_stss_temp tsjs
where 1=1
order by strs_stat_cd
