mapper.select:
SELECT CURR_FAT_JUDG_CD, fn_getCdNm('FAT_JUDG_CD', CURR_FAT_JUDG_CD) AS CURR_FAT_JUDG_NM, PRDT_FAT_JUDG_CD, fn_getCdNm('FAT_JUDG_CD', PRDT_FAT_JUDG_CD) AS PRDT_FAT_JUDG_NM, PAL_CD, fn_getCdNm('PAL_CD', PAL_CD) AS PAL_NM, SUBSTRING(FAT_ACT_RMRK,0,100) AS FAT_ACT_RMRK, REG_DT, REG_TM, REG_USER_ID, UPT_DT, UPT_TM, UPT_USER_ID FROM TI_FAT_ACT_STND WHERE 1 = 1
AND CURR_FAT_JUDG_CD = #{currFatJudgCd}
AND PRDT_FAT_JUDG_CD = #{prdtFatJudgCd}
AND PAL_CD = #{palCd}
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.insert:
INSERT INTO TI_FAT_ACT_STND
			(
			CURR_FAT_JUDG_CD,
			PRDT_FAT_JUDG_CD,
			PAL_CD,
			FAT_ACT_RMRK,
			REG_DT,
			REG_TM,
			REG_USER_ID,
			UPT_DT,
			UPT_TM,
			UPT_USER_ID
			)
VALUES
			(
			  #{currFatJudgCd}
			, #{prdtFatJudgCd}
			, #{palCd}
			, #{fatActRmrk}
			, TO_CHAR(NOW(), 'YYYYMMDD')
			, TO_CHAR(NOW(), 'HH24MISS')
			, #{regUserId}
			, TO_CHAR(NOW(), 'YYYYMMDD')
			, TO_CHAR(NOW(), 'HH24MISS')
			, #{uptUserId}
			)

mapper.update:
	UPDATE		TI_FAT_ACT_STND
	SET			UPT_DT           	= TO_CHAR(NOW(), 'YYYYMMDD')
				, UPT_TM			= TO_CHAR(NOW(),'HH24MISS')
				, UPT_USER_ID 		= #{uptUserId}
		, FAT_ACT_RMRK = #{fatActRmrk}
	WHERE		CURR_FAT_JUDG_CD	= #{currFatJudgCd}
    AND			PRDT_FAT_JUDG_CD	= #{prdtFatJudgCd}
	AND 		PAL_CD 				= #{palCd}

mapper.delete:
DELETE
FROM    	TI_FAT_ACT_STND
WHERE		CURR_FAT_JUDG_CD	= #{currFatJudgCd}
AND			PRDT_FAT_JUDG_CD	= #{prdtFatJudgCd}
AND 		PAL_CD 				= #{palCd}

mapper.select:
SELECT      CURR_FAT_JUDG_CD,
			PRDT_FAT_JUDG_CD,
    		PAL_CD
FROM        TI_FAT_ACT_STND
WHERE		CURR_FAT_JUDG_CD	= #{currFatJudgCd}
AND			PRDT_FAT_JUDG_CD	= #{prdtFatJudgCd}
AND 		PAL_CD 				= #{palCd}

SELECT CURR_FAT_JUDG_CD, fn_getCdNm('FAT_JUDG_CD', CURR_FAT_JUDG_CD) AS CURR_FAT_JUDG_NM, PRDT_FAT_JUDG_CD, fn_getCdNm('FAT_JUDG_CD', PRDT_FAT_JUDG_CD) AS PRDT_FAT_JUDG_NM, PAL_CD, fn_getCdNm('PAL_CD', PAL_CD) AS PAL_NM, FAT_ACT_RMRK, REG_DT, REG_TM, REG_USER_ID, UPT_DT, UPT_TM, UPT_USER_ID FROM TI_FAT_ACT_STND WHERE 1 = 1
AND CURR_FAT_JUDG_CD = #{currFatJudgCd}
AND PRDT_FAT_JUDG_CD = #{prdtFatJudgCd}
AND PAL_CD = #{palCd}
LIMIT ${rowCount} OFFSET ${currentIndex}
