mapper.update:
UPDATE TI_PAL_STND
SET         UPT_DT           =  TO_CHAR(NOW(), 'YYYYMMDD')
,  UPT_TM           = TO_CHAR(NOW(),'HH24MISS')
,  UPT_USER_ID  = #{uptUserId}
  			, PAL_NM = #{palNm}
		, PAL_VAL_FR = #{palValFr}::NUMERIC
		, PAL_VAL_TO = #{palValTo}::NUMERIC
	, PAL_EAT_RMRK = #{palEatRmrk}
WHERE  PAL_CD = #{palCd}

mapper.select:
SELECT      PAL_CD
FROM        TI_PAL_STND
WHERE     	PAL_CD = #{palCd}
