mapper.select:
SELECT DGEM_STAT_CD ,fn_getCdNm('DGEM_STAT_CD', DGEM_STAT_CD) AS DGEM_STAT_CD_NM , DGEM_STAT_CNTN , REG_DT , REG_TM , REG_USER_ID , UPT_DT , UPT_TM , UPT_USER_ID FROM TI_DGEM_STND WHERE 1 = 1
AND DGEM_STAT_CD = #{dgemStatCd} ORDER BY DGEM_STAT_CD
LIMIT ${rowCount} OFFSET ${currentIndex}
