mapper.select:
SELECT SEX_CD , fn_getCdNm('SEX_CD', SEX_CD) , AGE_YCNT_FR , AGE_YCNT_TO , CALC_FRML , REG_DT , REG_TM , REG_USER_ID , UPT_DT , UPT_TM , UPT_USER_ID FROM TI_BPAL_CALC_STND TBCS WHERE 1 = 1
AND SEX_CD = #{sexCd}
AND AGE_YCNT_FR = CAST (#{ageYcntFr} AS INTEGER) ORDER BY SEX_CD, AGE_YCNT_FR
LIMIT ${rowCount} OFFSET ${currentIndex}
