mapper.select:
SELECT ACT_CD , ACT_CD AS ACT_CD_TEMP , ACT_CLSS_CD , ACT_NM , ACT_DESC , MET_VAL , MET_MIN_CFCT , SORT_ORD , REG_DT , REG_TM , REG_USER_ID , UPT_DT , UPT_TM , UPT_USER_ID FROM TI_ACT_STND WHERE 1 = 1
AND ACT_NM LIKE CONCAT ('%',#{actNm},'%') ORDER BY SORT_ORD
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
	UPDATE TI_ACT_STND
	SET         UPT_DT           =  TO_CHAR(NOW(), 'YYYYMMDD')
              	,  UPT_TM           = TO_CHAR(NOW(),'HH24MISS')
             	,  UPT_USER_ID  = #{uptUserId}
	  ,ACT_CD = #{actCd}
	  ,ACT_CLSS_CD = #{actClssCd}
	, ACT_NM = #{actNm}
	, ACT_DESC = #{actDesc}
	, MET_VAL = #{metVal}::NUMERIC
	, MET_MIN_CFCT = #{metMinCfct}::NUMERIC
	, SORT_ORD = #{sortOrd}::NUMERIC
	WHERE  ACT_CD = #{actCdTemp}
