mapper.select:
SELECT ACT_CD , ACT_CLSS_CD , ACT_NM , ACT_DESC , MET_VAL , MET_MIN_CFCT , SORT_ORD , REG_DT , REG_TM , REG_USER_ID , UPT_DT , UPT_TM , UPT_USER_ID FROM TI_ACT_STND WHERE 1 = 1
AND ACT_CLSS_CD = #{actClssCd}
AND ACT_CD = #{actCd} ORDER BY SORT_ORD
LIMIT ${rowCount} OFFSET ${currentIndex}
