mapper.select:
SELECT GROW_STND_VER , GROW_STND_NO , GROW_STND_NO AS GROW_STND_NO_TEMP , SEX_CD , AGE_YCNT , AGE_MCNT , P3_GIDX , P50_GIDX , P97_GIDX , REG_DT , REG_TM , REG_USER_ID , UPT_DT , UPT_TM , UPT_USER_ID ,fn_getCdNm('SEX_CD', SEX_CD) FROM TI_GROW_STND WHERE 1 = 1
AND GROW_STND_VER = #{growStndVer}
AND AGE_YCNT = CAST (#{ageYcnt} AS INTEGER)
AND SEX_CD = #{sexCd} ORDER BY GROW_STND_VER, GROW_STND_NO
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
UPDATE TI_GROW_STND
SET         UPT_DT           =  TO_CHAR(NOW(), 'YYYYMMDD')
            ,  UPT_TM           = TO_CHAR(NOW(),'HH24MISS')
            ,  UPT_USER_ID  = #{uptUserId}
  ,GROW_STND_VER = #{growStndVer}
  ,GROW_STND_NO = #{growStndNo}
, SEX_CD = #{sexCd}
, AGE_YCNT = #{ageYcnt}
, AGE_MCNT = #{ageMcnt}
, P3_GIDX = #{p3Gidx}>
, P50_GIDX = #{p50Gidx}
, P97_GIDX = #{p97Gidx}
WHERE  GROW_STND_NO = #{growStndNoTemp}
