mapper.select:
with eat_stnd_info as ( select TDNES.sex_cd ,TDNES.age_ycnt ,case when TDNES.nutr_cd = 'CAL' then TDNES.dd_rcmd_qty end as CAL ,case when TDNES.nutr_cd = 'PRTN' then TDNES.dd_rcmd_qty end as PRTN ,case when TDNES.nutr_cd = 'FIDX' then TDNES.dd_rcmd_qty end as FIDX ,case when TDNES.nutr_cd = 'CARB' then TDNES.dd_rcmd_qty end as CARB ,case when TDNES.nutr_cd = 'DFIB' then TDNES.dd_rcmd_qty end as DFIB ,case when TDNES.nutr_cd = 'CA' then TDNES.dd_rcmd_qty end as CA ,case when TDNES.nutr_cd = 'FE' then TDNES.dd_rcmd_qty end as FE ,case when TDNES.nutr_cd = 'MG' then TDNES.dd_rcmd_qty end as MG ,case when TDNES.nutr_cd = 'NA' then TDNES.dd_rcmd_qty end as NA ,case when TDNES.nutr_cd = 'ZN' then TDNES.dd_rcmd_qty end as ZN ,case when TDNES.nutr_cd = 'VIT_D3' then TDNES.dd_rcmd_qty end as VIT_D3 ,case when TDNES.nutr_cd = 'VIT_B' then TDNES.dd_rcmd_qty end as VIT_B ,case when TDNES.nutr_cd = 'VIT_B1' then TDNES.dd_rcmd_qty end as VIT_B1 ,case when TDNES.nutr_cd = 'VIT_B2' then TDNES.dd_rcmd_qty end as VIT_B2 ,case when TDNES.nutr_cd = 'NIA' then TDNES.dd_rcmd_qty end as NIA ,case when TDNES.nutr_cd = 'DFE' then TDNES.dd_rcmd_qty end as DFE ,case when TDNES.nutr_cd = 'VIT_B12' then TDNES.dd_rcmd_qty end as VIT_B12 ,case when TDNES.nutr_cd = 'ILE' then TDNES.dd_rcmd_qty end as ILE ,case when TDNES.nutr_cd = 'LEU' then TDNES.dd_rcmd_qty end as LEU ,case when TDNES.nutr_cd = 'VAL' then TDNES.dd_rcmd_qty end as VAL ,case when TDNES.nutr_cd = 'AMNO' then TDNES.dd_rcmd_qty end as AMNO ,case when TDNES.nutr_cd = 'CHL' then TDNES.dd_rcmd_qty end as CHL ,case when TDNES.nutr_cd = 'FAPU' then TDNES.dd_rcmd_qty end as FAPU ,case when TDNES.nutr_cd = 'EPA' then TDNES.dd_rcmd_qty end as EPA ,case when TDNES.nutr_cd = 'DHA' then TDNES.dd_rcmd_qty end as DHA ,case when TDNES.nutr_cd = 'EPA_DHA' then TDNES.dd_rcmd_qty end as EPA_DHA from TI_DD_NUTR_EAT_STND TDNES INNER JOIN TI_NUTR_INFO AS TNI ON TDNES.NUTR_CD = TNI.NUTR_CD ) select sex_cd ,age_ycnt ,MAX(CAL) AS CAL ,MAX(PRTN) AS PRTN ,MAX(FIDX) AS FIDX ,MAX(CARB) AS CARB ,MAX(DFIB) AS DFIB ,MAX(CA) AS CA ,MAX(FE) AS FE ,MAX(MG) AS MG ,MAX(NA) AS NA ,MAX(ZN) AS ZN ,MAX(VIT_D3) AS VIT_D3 ,MAX(VIT_B) AS VIT_B ,MAX(VIT_B1) AS VIT_B1 ,MAX(VIT_B2) AS VIT_B2 ,MAX(NIA) AS NIA ,MAX(DFE) AS DFE ,MAX(VIT_B12) AS VIT_B12 ,MAX(ILE) AS ILE ,MAX(LEU) AS LEU ,MAX(VAL) AS VAL ,MAX(AMNO) AS AMNO ,MAX(CHL) AS CHL ,MAX(FAPU) AS FAPU ,MAX(EPA) AS EPA ,MAX(DHA) AS DHA ,MAX(EPA_DHA) AS EPA_DHA from eat_stnd_info WHERE 1 = 1
AND SEX_CD = #{sexCd}
AND AGE_YCNT = #{ageYcnt}::NUMERIC group by sex_cd , age_ycnt order by sex_cd , age_ycnt

mapper.delete:
        DELETE
        FROM    TI_DD_NUTR_EAT_STND
        WHERE  1=1
		AND SEX_CD = #{sexCdTemp}
		AND AGE_YCNT = #{ageYcntTemp}::NUMERIC
		AND NUTR_CD = #{nutrCdTemp}
