mapper.select:
SELECT TDNEB.SEX_CD AS SEX_CD_TEMP , TDNEB.AGE_YCNT AS AGE_YCNT_TEMP , TNI.NUTR_CD AS NUTR_CD_TEMP , TDNEB.EAT_QTY_FR AS EAT_QTY_FR_TEMP , TDNEB.SEX_CD , TDNEB.AGE_YCNT , TNI.NUTR_CD , TNI.NUTR_NM , TDNEB.EAT_QTY_FR , TDNEB.EAT_QTY_TO , TDNEB.NUTR_STAT_CD , TDNEB.USE_YN , TDNEB.REG_DT , TDNEB.REG_TM , TDNEB.REG_USER_ID , TDNEB.UPT_DT , TDNEB.UPT_TM , TDNEB.UPT_USER_ID FROM TI_DD_NUTR_EAT_BLCK AS TDNEB LEFT OUTER JOIN TI_NUTR_INFO AS TNI ON TDNEB.NUTR_CD = TNI.NUTR_CD WHERE 1 = 1
AND SEX_CD = #{sexCd}
AND AGE_YCNT = #{ageYcnt}::NUMERIC
AND NUTR_NM LIKE CONCAT ('%', #{nutrNm}, '%')
AND EAT_QTY_FR = #{eatQtyFr}::NUMERIC ORDER BY SEX_CD, AGE_YCNT,NUTR_NM
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.update:
	UPDATE TI_DD_NUTR_EAT_BLCK
	SET         UPT_DT           = TO_CHAR(NOW(), 'YYYYMMDD')
	            ,  UPT_TM           = TO_CHAR(NOW(),'HH24MISS')
             	,  UPT_USER_ID  = #{uptUserId}
	  ,SEX_CD = #{sexCd}
	  ,AGE_YCNT = #{ageYcnt}::NUMERIC
	  ,NUTR_CD = #{nutrCd}
	  ,EAT_QTY_FR = #{eatQtyFr}::NUMERIC
	  ,EAT_QTY_TO = #{eatQtyTo}::NUMERIC
	, NUTR_STAT_CD = #{nutrStatCd}
	, USE_YN = #{useYn}
	WHERE  1=1
	AND SEX_CD         = #{sexCdTemp}
	AND AGE_YCNT    = #{ageYcntTemp}::NUMERIC
	AND NUTR_CD      = #{nutrCdTemp}
	AND EAT_QTY_FR  = #{eatQtyFrTemp}::NUMERIC
