mapper.insert:

	select nextval('absv.tm_guar_base_seq')

INSERT INTO TM_GUAR_BASE
(                                           guar_no 		   /* guar_no 보호자_번호 numeric */
	                                      , guar_nm 		   /* guar_nm 보호자_명 character varying(20) */
