mapper.update:
UPDATE TM_GUAR_BASE
SET
 													          UPT_DT = TO_CHAR(NOW(), 'YYYYMMDD')
    													    , UPT_TM = TO_CHAR(NOW(),'HH24MISS')
    													    , UPT_USER_ID       = #{uptUserId}
 , guar_no           = #{guarNo}            	      
 , guar_nm           = #{guarNm}				      
 , guar_tel_no       = #{guarTelNo}				  
 , guar_pw           = #{guarPw}				      
 , self_cert_dttm    = #{selfCertDttm}	    	  
 , auto_login_yn     = #{autoLoginYn}			      
 , devc_cert_val     = #{devcCertVal}			      
 , race_div_cd       = #{raceDivCd}		    	  
 , sex_cd 	       = #{sexCd}				      
 , hght_val	  	   = #{hghtVal}::numeric    	  
 , wght_val 	   	   = #{wghtVal}::numeric 		  
 , bmi_val           = #{bmiVal}::numeric  		  
 , spos_no           = #{sposNo}::numeric   	      
 , dzone_alam_yn     = #{dzoneAlamYn}       	      
 , szone_alam_yn     = #{szoneAlamYn}   	    	  
 , fall_alam_yn      = #{fallAlamYn}    	    	  
 , hbit_abnm_alam_yn = #{hbitAbnmAlamYn}	    	  
 , temp_abnm_alam_yn = #{tempAbnmAlamYn}	    	  
 , body_hist_alam_yn = #{bodyHistAlamYn}	    	  
 , meal_noin_alam_yn = #{mealNoinAlamYn}	    	  
 , excs_noin_alam_yn = #{excsNoinAlamYn}	    	  
 , cbee_use_alam_yn  = #{cbeeUseAlamYn} 	    	  
 , batr_lack_alam_yn = #{batrLackAlamYn}	    	  
 , ltrm_nuse_alam_yn = #{ltrmNuseAlamYn}	    	  
 , entr_dt	       = replace(#{entrDt} , '-','')  
 , rels_dt 	       = replace(#{relsDt} , '-','')  
 , rels_resn_cd 	   = #{relsResnCd}   			  
 , rels_resn_cntn    = #{relsResnCntn}			  
 , entr_stat_cd 	   = #{entrStatCd}				  
WHERE 1=1
AND GUAR_NO = #{guarNo}::NUMERIC
