mapper.select:
select TGB.GUAR_NO as GUAR_NO_TEMP -- 보호자_번호_임시 , TSB.STDT_NO -- 학생_번호 , TSB.STDT_NM -- 학생_명 , TGB.ENTR_DT -- 가입_일자 , TBI.TEL_NO -- BAND전화_번호 , TEB.LOC_NM -- 위치_명 , TBI.BAND_ID -- 밴드ID , TGB.GUAR_NO -- 보호자_번호 , TGB.GUAR_NM -- 보호자_명 , TGB.GUAR_TEL_NO -- 보호자_전화_번호 , TGB.SPOS_NO -- 배우자_번호 , TSB2.SPOS_NM -- 배우자_명 , TSB2.SPOS_TEL_NO -- 배우자_전화_번호 , TSB.EORG_LOC_NO -- 교육시설_위치_번호 , fn_getCdNm('TERM_DIV_CD', TERM_DIV_CD) as TERM_DIV_CD -- 약관_구분_명 , TTAD.TERM_AGRE_YN-- 약관_동의_여부 from TM_GUAR_BASE TGB left outer join TM_STDT_BASE TSB on TGB.GUAR_NO = TSB.GUAR_NO_1 or TGB.GUAR_NO = TSB.GUAR_NO_2 left outer JOIN TM_SPOS_BASE TSB2 on TGB.SPOS_NO = TSB2.SPOS_NO left outer JOIN TI_EORG_BASE TEB on TSB.EORG_LOC_NO = TEB.LOC_NO left outer JOIN TM_TERM_AGRE_DETL TTAD on TGB.GUAR_NO = TTAD.GUAR_NO left outer JOIN TS_BAND_INFO TBI on TSB.BAND_ID = TBI.BAND_ID where 1=1
and TGB.ENTR_DT between #{entrFr} and #{entrTo}
and TSB.STDT_NO = #{stdtNo}::NUMERIC
and TSB.STDT_NM like CONCAT('%',#{stdtNm},'%')
and TBI.BAND_ID like CONCAT('%',#{bandId},'%')
and TBI.TEL_NO = #{telNo}
and TGB.GUAR_NO = #{guarNo}::NUMERIC
and TGB.GUAR_NM like CONCAT('%',#{guarNm},'%')
and TGB.GUAR_TEL_NO = #{guarTelNo} ORDER BY TSB.STDT_NO, TSB.STDT_NM, TGB.ENTR_DT, TEB.LOC_NM, TBI.BAND_ID, TGB.GUAR_NO,TGB.GUAR_NM, TGB.SPOS_NO , TSB2.SPOS_NM , TSB2.SPOS_TEL_NO
LIMIT ${rowCount} OFFSET ${currentIndex}

mapper.insert:
INSERT INTO TS_BAND_INFO
(
BAND_ID
, BAND_MDL_CD
, TEL_NO
, BLTH_ID
, GUAR_TEL_NO
, API_URL_GRAM_NO
, API_URL_DTTM
, OPEN_GRAM_NO
, BAND_OPEN_STAT_CD
, REG_DT
, REG_TM
, REG_USER_ID
, UPT_DT
, UPT_TM
, UPT_USER_ID
)
VALUES
(
#{bandId}
, #{bandMdlCd}
, #{telNo}
, #{blthId}
, #{guarTelNo}
, #{apiUrlGramNo}
, #{apiUrlDttm}
, #{openGramNo}
, #{bandOpenStatCd}
, TO_CHAR(NOW(), 'YYYYMMDD')
, TO_CHAR(NOW(), 'HH24MISS')
, #{regUserId}
, TO_CHAR(NOW(), 'YYYYMMDD')
, TO_CHAR(NOW(), 'HH24MISS')
, #{uptUserId}
)

mapper.update:
UPDATE TS_BAND_INFO
SET UPT_DT = TO_CHAR(NOW(), 'YYYYMMDD')
, UPT_TM = TO_CHAR(NOW(),'HH24MISS')
, UPT_USER_ID = #{uptUserId}
 ,TEL_NO = #{telNo}
 ,GUAR_TEL_NO = #{guarTelNo}
 ,BAND_OPEN_STAT_CD = #{bandOpenStatCd}
 ,BLTH_ID = #{blthId}
WHERE 1=1
AND BAND_ID = #{bandId}
